CREATE TABLE bild (id INTEGER PRIMARY KEY AUTOINCREMENT, path VARCHAR(255) NOT NULL UNIQUE, caption VARCHAR(255), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL);
CREATE TABLE stunde (id INTEGER PRIMARY KEY AUTOINCREMENT, description VARCHAR(255) NOT NULL, zim_id INTEGER NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL);
CREATE TABLE zim (id INTEGER PRIMARY KEY AUTOINCREMENT, name VARCHAR(255) NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL);
CREATE TABLE zim_anlage (id INTEGER PRIMARY KEY AUTOINCREMENT, name VARCHAR(255) NOT NULL UNIQUE, zeit INTEGER, ziel VARCHAR(255), methode VARCHAR(255), material VARCHAR(1000), tip VARCHAR(255), kurzinhalt VARCHAR(1000), inhalt VARCHAR(10000), rolle_tm VARCHAR(255), bild_id INTEGER, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL);
